import Link from "next/link";

const Page = () => (
  <div>
    <div id="carousel" className="carousel">
      <div className="slides">
        <div className="slide" data-state="active">
          Slide 1
        </div>
        <div className="slide">Slide 2</div>
        <div className="slide">Slide 3</div>
      </div>
      <div className="indicators">
        <input
          className="indicator"
          name="indicator"
          data-slide="1"
          data-state="active"
          checked
          type="radio"
        />
        <input
          className="indicator"
          name="indicator"
          data-slide="2"
          type="radio"
        />
        <input
          className="indicator"
          name="indicator"
          data-slide="3"
          type="radio"
        />
      </div>
    </div>
    <style jsx>{`
      .carousel {
        position: relative;
        height: 400px;
        width: 750px;
      }

      .slides {
        height: 100%;
        width: 100%;
        position: relative;
      }

      .slide {
        position: absolute;
        top: 0;
        left: 0;
        height: 100%;
        width: 100%;
        font-size: 3em;
        color: white;
        line-height: 400px;
        text-align: center;
        opacity: 0;
        transition: opacity 1000ms;
      }

      .slide:nth-child(1) {
        background-color: red;
        opacity: 1;
      }

      .slide:nth-child(2) {
        background-color: green;
      }

      .slide:nth-child(3) {
        background-color: blue;
      }

      .slide[data-state="active"] {
        display: block;
      }

      .indicators {
        position: absolute;
        bottom: 20px;
        right: 20px;
      }

      .indicator {
        cursor: pointer;
      }
    `}</style>
  </div>
);

var carousel = document.getElementById("carousel");
var slides = 3;
var speed = 7000; // 5 seconds

function carouselHide(num) {
  indicators[num].setAttribute("data-state", "");
  slides[num].setAttribute("data-state", "");

  slides[num].style.opacity = 0;
}

function carouselShow(num) {
  indicators[num].checked = true;
  indicators[num].setAttribute("data-state", "active");
  slides[num].setAttribute("data-state", "active");

  slides[num].style.opacity = 1;
}

function setSlide(slide) {
  return function () {
    // Reset all slides
    for (var i = 0; i < indicators.length; i++) {
      indicators[i].setAttribute("data-state", "");
      slides[i].setAttribute("data-state", "");

      carouselHide(i);
    }

    // Set defined slide as active
    indicators[slide].setAttribute("data-state", "active");
    slides[slide].setAttribute("data-state", "active");
    carouselShow(slide);

    // Stop the auto-switcher
    clearInterval(switcher);
  };
}

function switchSlide() {
  var nextSlide = 0;

  // Reset all slides
  for (var i = 0; i < indicators.length; i++) {
    // If current slide is active & NOT equal to last slide then increment nextSlide
    if (
      indicators[i].getAttribute("data-state") == "active" &&
      i !== indicators.length - 1
    ) {
      nextSlide = i + 1;
    }

    // Remove all active states & hide
    carouselHide(i);
  }

  // Set next slide as active & show the next slide
  carouselShow(nextSlide);
}

if (carousel) {
  var slides = carousel.querySelectorAll(".slide");
  var indicators = carousel.querySelectorAll(".indicator");

  var switcher = setInterval(function () {
    switchSlide();
  }, speed);

  for (var i = 0; i < indicators.length; i++) {
    indicators[i].addEventListener("click", setSlide(i));
  }
}

export default Page;
