import Link from "next/link";

const Page = () => (
  <section>
    <div class="slider">
        <input type="radio" name="slider" title="slide1" checked="checked" class="slider__nav"/>
        <input type="radio" name="slider" title="slide2" class="slider__nav"/>
        <input type="radio" name="slider" title="slide3" class="slider__nav"/>
        <input type="radio" name="slider" title="slide4" class="slider__nav"/>
        <div class="slider__inner">
            <div class="slider__contents"><i class="slider__image fa fa-codepen"></i>
            <h2 class="slider__caption">codepen</h2>
            <p class="slider__txt">Lorem ipsum dolor sit amet, consectetur adipisicing elit. At cupiditate omnis possimus illo quos, corporis minima!</p>
        </div>
        <div class="slider__contents"><i class="slider__image fa fa-newspaper-o"></i>
        <h2 class="slider__caption">newspaper-o</h2>
        <p class="slider__txt">Lorem ipsum dolor sit amet, consectetur adipisicing elit. At cupiditate omnis possimus illo quos, corporis minima!</p>
        </div>
        <div class="slider__contents"><i class="slider__image fa fa-television"></i>
        <h2 class="slider__caption">television</h2>
        <p class="slider__txt">Lorem ipsum dolor sit amet, consectetur adipisicing elit. At cupiditate omnis possimus illo quos, corporis minima!</p>
        </div>
        <div class="slider__contents"><i class="slider__image fa fa-diamond"></i>
        <h2 class="slider__caption">diamond</h2>
        <p class="slider__txt">Lorem ipsum dolor sit amet, consectetur adipisicing elit. At cupiditate omnis possimus illo quos, corporis minima!</p>
        </div>
    </div>
    </div>
    <style jsx>{`
      $blockWidth: 150px;
      $blockHeight: 75px;

      @mixin animated($name) {
        -webkit-animation-name: $name;
        animation-name: $name;
      }

      @mixin keyFramed($vals, $valsTop) {
        0% {
          left: nth($vals, 1);
          top: nth($valsTop, 1);
        }
        50.01% {
          left: nth($vals, 4);
          top: nth($valsTop, 4);
        }
        100% {
          left: nth($vals, 1);
          top: nth($valsTop, 1);
        }
      }

      @mixin myKeyFrame($number, $pos, $posTop) {
        @keyframes slide#{$number} {
          @include keyFramed($pos, $posTop);
        }

        @-webkit-keyframes slide#{$number} {
          @include keyFramed($pos, $posTop);
        }
      }

      $negativeBlockWidth: -1 * $blockWidth;

      $posFirst: 0px, $negativeBlockWidth, $negativeBlockWidth, 0px, $blockWidth,
        $blockWidth;
      $posSecond: $blockWidth, 0px, $negativeBlockWidth, $negativeBlockWidth,
        0px, $blockWidth;


      $negativeBlockHeight: -1 * $blockHeight;

      $posFirstTop: 0px, 0px, $negativeBlockHeight, $negativeBlockHeight,
        $negativeBlockHeight, 0px;
      $posSecondTop: 0px, 0px, 0px, $negativeBlockHeight, $negativeBlockHeight,
        $negativeBlockHeight;

      .carousel {
        position: relative;
        margin: 100px auto;
        width: $blockWidth;
        height: $blockHeight;
        overflow: hidden;
      }

      .slide {
        width: $blockWidth;
        height: $blockHeight;
        position: absolute;
        top: 0px;
        -webkit-animation-iteration-count: infinite;
        animation-iteration-count: infinite;
        -webkit-animation-duration: 15s;
        animation-duration: 15s;
        -webkit-animation-delay: 2s;
        animation-delay: 5s;
        -webkit-animation-timing-function: ease;
        animation-timing-function: ease;
        -webkit-animation-direction: normal;
        animation-direction: normal;
      }

      #slide1 {
        @include animated(slide1);
        left: 0px;
        top: 0px;
      }
      #slide2 {
        @include animated(slide2);
        left: $blockWidth;
        top: 0px;
      }

      @include myKeyFrame(1, $posFirst, $posFirstTop);
      @include myKeyFrame(2, $posSecond, $posSecondTop);
    `}</style>
  </section>
);

export default Page;
