import Link from "next/link";

const Page = () => (
  <div>
    <div id="carousel" className="carousel">
      <div className="slides">
        <div className="slide" data-state="active">
          Slide 1
        </div>
        <div className="slide">Slide 2</div>
        <div className="slide">Slide 3</div>
      </div>
      <div className="indicators">
        <input
          className="indicator"
          name="indicator"
          data-slide="1"
          data-state="active"
          checked
          type="radio"
        />
        <input
          className="indicator"
          name="indicator"
          data-slide="2"
          type="radio"
        />
        <input
          className="indicator"
          name="indicator"
          data-slide="3"
          type="radio"
        />
      </div>
    </div>
    <style jsx>{`
      .carousel {
        position: relative;
        height: 400px;
        width: 750px;
      }

      .slides {
        height: 100%;
        width: 100%;
        position: relative;
      }

      .slide {
        position: absolute;
        top: 0;
        left: 0;
        height: 100%;
        width: 100%;
        font-size: 3em;
        color: white;
        line-height: 400px;
        text-align: center;
        opacity: 0;
        transition: opacity 1000ms;
      }

      .slide:nth-child(1) {
        background-color: red;
        opacity: 1;
      }

      .slide:nth-child(2) {
        background-color: green;
      }

      .slide:nth-child(3) {
        background-color: blue;
      }

      .slide[data-state="active"] {
        display: block;
      }

      .indicators {
        position: absolute;
        bottom: 20px;
        right: 20px;
      }

      .indicator {
        cursor: pointer;
      }
    `}</style>
  </div>
);



export default Page;
